const timeout = 10;let theme = window.localStorage.getItem("swnthemev1");function setTheme(theme) {document.body.classList.add(theme);};function waitForBody(){if(document.body){setTheme(theme.toLowerCase());return;};setTimeout(()=>{waitForBody(); return;},timeout);};if(theme==null){window.localStorage.setItem("swnthemev1",'dark');theme='dark'};if(document.body){setTheme(theme.toLowerCase());}else {waitForBody();};